struct VgContext {
	Level level;
	Logic logic;
	long frames;
	string graphics_json;
	
	VgContext () {
		emscripten_wget ("maps/demo.bin", "maps/demo.bin");
		level = Level (lookup_file ("maps/demo.bin"));
		//level = Level (vector <uint8_t> (&map_binary [0], &map_binary [4100]));
		logic = Logic (level);
		frames = 0;
	}
	
	void step () {
		InputFrame input_frame;
		logic.step (input_frame, frames);
		
		frames++;
	}
	
	const char * get_graphics_json () {
		GraphicsEcs graphics_ecs;
		graphics_ecs = animate_vegicide (logic.scene, logic.level, frames, 800.0 / 480.0);
		
		stringstream s;
		to_json (s, graphics_ecs);
		
		graphics_json = s.str ();
		
		return graphics_json.c_str ();
	}
};

VgHandle vegicide_init () {
	global = new VgContext ();
	return global;
}

void vegicide_step () {
	if (global != nullptr) {
		VgContext & ctx = *(VgContext *)global;
		ctx.step ();
	}
}

const char * vegicide_get_graphics_json () {
	if (global != nullptr) {
		VgContext & ctx = *(VgContext *)global;
		return ctx.get_graphics_json ();
	}
	return "{}";
}
